import React from "react";
import MovieCard from "./MovieCard"; // Assuming a MovieCard component will be created

/**
 * Props:
 * - items: array film (objek dengan imdbID, Title, Year, Poster, imdbRating, Genre, Plot)
 * - loading: boolean
 * - error: string|null
 * - page: current page number
 * - totalResults: total number of results
 * - onSelect(item): open detail
 * - onToggleFav(item): toggle favorite
 * - favorites: array of favorite items
 * - onPageChange(page): function to change page
 */
export default function MovieGrid({
  items = [],
  loading,
  error,
  page,
  totalResults,
  onSelect,
  onToggleFav,
  favorites = [],
  onPageChange,
}) {
  const isFav = (item) => favorites.some((f) => f.imdbID === item.imdbID);
  const totalPages = Math.max(1, Math.ceil(totalResults / 10));

  return (
    <div className="movie-grid-wrapper">
      <h2 className="visually-hidden">Hasil Pencarian Film</h2>

      {loading && <div className="grid-loading">Memuat hasil...</div>}
      {error && <div className="grid-error">{error}</div>}

      {!loading && !error && items.length === 0 && (
        <div className="grid-empty">Belum ada hasil. Coba kata kunci lain.</div>
      )}

      {!loading && items.length > 0 && (
        <>
          <div className="movie-grid" role="grid" aria-label="Hasil pencarian film">
            {items.map((item) => (
              <MovieCard
                key={item.imdbID}
                movie={item}
                onSelect={onSelect}
                onToggleFav={onToggleFav}
                isFav={isFav(item)}
              />
            ))}
          </div>

          {/* Pagination controls */}
          {totalResults > 0 && (
            <div className="pagination" style={{ display: "flex", justifyContent: "center", gap: 12, marginTop: 16 }}>
              <button className="btn ghost" disabled={page <= 1} onClick={() => onPageChange(page - 1)}>
                Previous
              </button>
              <div className="page-info">
                Page {page} of {totalPages}
              </div>
              <button
                className="btn ghost"
                disabled={page >= totalPages}
                onClick={() => onPageChange(page + 1)}
              >
                Next
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}
