import React, { useState, useMemo } from "react";

/**
 * Props:
 * - items: array film (objek dengan imdbID, Title, Year, Poster, imdbRating, Genre, Plot)
 * - loading: boolean
 * - error: string|null
 * - onSelect(item): open detail
 * - onToggleFav(item): toggle favorite
 * - favorites: array of favorite items
 */
export default function DataTable({ items = [], loading, error, onSelect, onToggleFav, favorites = [] }) {
  const [sortBy, setSortBy] = useState({ key: "Title", dir: "asc" });

  const toggleSort = (key) => {
    setSortBy(prev => {
      if (prev.key === key) {
        return { key, dir: prev.dir === "asc" ? "desc" : "asc" };
      }
      return { key, dir: "asc" };
    });
  };

  const sorted = useMemo(() => {
    const arr = [...items];
    const key = sortBy.key;
    arr.sort((a, b) => {
      const A = (a[key] ?? "").toString();
      const B = (b[key] ?? "").toString();
      if (key === "imdbRating") {
        return (parseFloat(A) || 0) - (parseFloat(B) || 0);
      }
      if (key === "Year") {
        return (parseInt(A) || 0) - (parseInt(B) || 0);
      }
      return A.localeCompare(B);
    });
    if (sortBy.dir === "desc") arr.reverse();
    return arr;
  }, [items, sortBy]);

  const isFav = (item) => favorites.some(f => f.imdbID === item.imdbID);

  return (
    <div className="data-table-wrapper">
      <h2 className="visually-hidden">Tabel Hasil Pencarian</h2>

      {loading && <div className="dt-loading">Memuat hasil...</div>}
      {error && <div className="dt-error">{error}</div>}

      {!loading && !error && sorted.length === 0 && (
        <div className="dt-empty">Belum ada hasil. Coba kata kunci lain.</div>
      )}

      {!loading && sorted.length > 0 && (
        <div className="table-responsive">
          <table className="data-table" role="table" aria-label="Hasil pencarian film">
            <thead>
              <tr>
                <th scope="col">Poster</th>
                <th scope="col" className="sortable" onClick={() => toggleSort("Title")}>
                  Judul <SortChevron active={sortBy.key === "Title"} dir={sortBy.dir} />
                </th>
                <th scope="col" className="sortable" onClick={() => toggleSort("Year")}>
                  Tahun <SortChevron active={sortBy.key === "Year"} dir={sortBy.dir} />
                </th>
                <th scope="col" className="sortable" onClick={() => toggleSort("imdbRating")}>
                  Rating <SortChevron active={sortBy.key === "imdbRating"} dir={sortBy.dir} />
                </th>
                <th scope="col">Aksi</th>
              </tr>
            </thead>

            <tbody>
              {sorted.map(item => (
                <tr key={item.imdbID} className="dt-row">
                  <td className="dt-poster" onClick={() => onSelect(item)}>
                    <img
                      src={item.Poster && item.Poster !== "N/A" ? item.Poster : "https://via.placeholder.com/80x110?text=No+Image"}
                      alt={item.Title}
                      width="72"
                      height="100"
                    />
                  </td>

                  <td className="dt-title" onClick={() => onSelect(item)} role="cell">
                    <button className="link-like" onClick={(e) => { e.preventDefault(); onSelect(item); }}>
                      <strong>{item.Title}</strong>
                    </button>
                    <div className="dt-genre">{item.Genre || ""}</div>
                  </td>

                  <td onClick={() => onSelect(item)}>{item.Year}</td>

                  <td onClick={() => onSelect(item)}>{item.imdbRating || "N/A"}</td>

                  <td>
                    <div className="dt-actions">
                      <button
                        className={`btn small ${isFav(item) ? "danger" : ""}`}
                        onClick={(e) => { e.stopPropagation(); onToggleFav(item); }}
                        aria-pressed={isFav(item)}
                        aria-label={isFav(item) ? "Hapus dari favorit" : "Tambah ke favorit"}
                      >
                        {isFav(item) ? "Hapus Fav" : "Tambah Fav"}
                      </button>

                      <button
                        className="btn ghost small"
                        onClick={(e) => { e.stopPropagation(); onSelect(item); }}
                      >
                        Detail
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

/* small helper component for chevron */
function SortChevron({ active, dir }) {
  return (
    <span className={`sort-chevron ${active ? "active" : ""}`} aria-hidden="true">
      {active ? (dir === "asc" ? "▲" : "▼") : "▾"}
    </span>
  );
}
