import React from "react";

/**
 * Props:
 * - movie: object film (imdbID, Title, Year, Poster, imdbRating, Genre, Plot)
 * - onSelect(item): open detail
 * - onToggleFav(item): toggle favorite
 * - isFav: boolean, apakah film sudah favorit
 */
export default function MovieCard({ movie, onSelect, onToggleFav, isFav }) {
  return (
    <div className="movie-card" role="gridcell" aria-label={`Film ${movie.Title}`}>
      <div className="card-poster" onClick={() => onSelect(movie)}>
        <img
          src={movie.Poster && movie.Poster !== "N/A" ? movie.Poster : "https://via.placeholder.com/150x220?text=No+Image"}
          alt={`${movie.Title} poster`}
          width="150"
          height="220"
        />
      </div>
      <div className="card-info">
        <h3 className="card-title" onClick={() => onSelect(movie)}>
          <button className="link-like" onClick={(e) => { e.preventDefault(); onSelect(movie); }}>
            {movie.Title}
          </button>
        </h3>
        <div className="card-meta">
          <span className="card-year">{movie.Year}</span>
          <span className="card-rating">‚≠ê {movie.imdbRating || "N/A"}</span>
        </div>
        <div className="card-actions">
          <button
            className={`btn small ${isFav ? "danger" : ""}`}
            onClick={(e) => { e.stopPropagation(); onToggleFav(movie); }}
            aria-pressed={isFav}
            aria-label={isFav ? "Hapus dari favorit" : "Tambah ke favorit"}
          >
            {isFav ? "Hapus Fav" : "Tambah Fav"}
          </button>
          <button
            className="btn ghost small"
            onClick={(e) => { e.stopPropagation(); onSelect(movie); }}
          >
            Detail
          </button>
        </div>
      </div>
    </div>
  );
}
