import React, { useState, useEffect, useRef } from "react";

/**
 * Props:
 * - onSearch(q, y, page)  -> dipanggil saat user submit atau debounce selesai
 * - initialQuery, initialYear (optional)
 */
export default function SearchForm({ onSearch, initialQuery = "", initialYear = "" }) {
  const [q, setQ] = useState(initialQuery);
  const [y, setY] = useState(initialYear);
  const [typing, setTyping] = useState(false);
  const [showClear, setShowClear] = useState(false);
  const debounceRef = useRef(null);

  useEffect(() => {
    setQ(initialQuery);
    setY(initialYear);
  }, [initialQuery, initialYear]);

  // Debounce auto-search ketika mengetik (600ms)
  useEffect(() => {
    if (!q.trim()) {
      setShowClear(false);
    } else {
      setShowClear(true);
    }

    setTyping(true);
    clearTimeout(debounceRef.current);
    debounceRef.current = setTimeout(() => {
      if (q.trim()) {
        onSearch(q.trim(), y.trim(), 1);
      } else {
        // jika input kosong, panggil onSearch dengan empty untuk clear results
        onSearch("", "", 1);
      }
      setTyping(false);
    }, 600);

    return () => clearTimeout(debounceRef.current);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [q, y]);

  function handleSubmit(e) {
    e.preventDefault();
    clearTimeout(debounceRef.current);
    onSearch(q.trim(), y.trim(), 1);
    setTyping(false);
  }

  function handleClear() {
    setQ("");
    setY("");
    setShowClear(false);
    clearTimeout(debounceRef.current);
    onSearch("", "", 1);
  }

  return (
    <form className="search-form" onSubmit={handleSubmit} role="search" aria-label="Form pencarian film">
      <div className="input-group">
        <label htmlFor="movie-query" className="sr-only">Cari judul film</label>
        <input
          id="movie-query"
          type="search"
          inputMode="search"
          placeholder="Cari judul film... (mis. The Matrix)"
          value={q}
          onChange={(e) => setQ(e.target.value)}
          autoComplete="off"
          aria-label="Cari judul film"
        />

        <button
          type="button"
          className={`clear-btn ${showClear ? "visible" : ""}`}
          onClick={handleClear}
          aria-label="Bersihkan pencarian"
          title="Bersihkan"
        >
          âœ•
        </button>
      </div>

      <div className="input-group small">
        <label htmlFor="movie-year" className="sr-only">Tahun</label>
        <input
          id="movie-year"
          type="number"
          placeholder="Tahun (opsional)"
          value={y}
          onChange={(e) => setY(e.target.value)}
          min="1880"
          max={new Date().getFullYear()}
          aria-label="Tahun film (opsional)"
        />
      </div>

      <button className="btn" type="submit" aria-label="Cari">Cari</button>

      <div className="search-meta">
        <span className="typing-indicator" aria-live="polite">{typing ? "Mengetik..." : ""}</span>
        <button
          type="button"
          className="btn ghost small"
          onClick={() => { setQ(""); onSearch("", y.trim(), 1); }}
          title="Reset kueri (tetap simpan tahun)"
        >
          Reset
        </button>
      </div>
    </form>
  );
}
